mapper.select:
SELECT *  FROM file_share a
LEFT JOIN file_sharefile b ON b.shareBatchNum = a.shareBatchNum
LEFT JOIN file_userfile c ON c.userFileId = b.userFileId
left JOIN file_file d ON d.fileId = c.fileId
WHERE shareFilePath = #{shareFilePath}

    AND a.shareBatchNum = #{shareBatchNum}

AND c.userId = #{userId}
order BY shareTime desc
limit #{beginCount}, #{pageCount}

SELECT count(*)  FROM file_share a
LEFT JOIN file_sharefile b ON b.shareBatchNum = a.shareBatchNum
LEFT JOIN file_userfile c ON c.userFileId = b.userFileId
left JOIN file_file d ON d.fileId = c.fileId
WHERE shareFilePath = #{shareFilePath}

    AND a.shareBatchNum = #{shareBatchNum}

AND c.userId = #{userId}
