mapper.select:
select * from file_userfile a
left JOIN file_file on file_file.fileId = a.fileId
left join file_image on a.fileId = file_image.fileId


        and a.userId = #{userFile.userId}


        and a.filePath = #{userFile.filePath}


        and a.extendName = #{userFile.extendName}


        and a.userFileId = #{userFile.userFileId}


        and a.fileName = #{userFile.fileName}

    and a.deleteFlag = 0

ORDER BY isDir desc

    limit #{beginCount}, #{pageCount}

mapper.sql:
left JOIN file_file on file_file.fileId = file_userfile.fileId
where extendName in

    #{fileName}

and userId = #{userId}
and deleteFlag = 0

left JOIN file_file on file_file.fileId = file_userfile.fileId
where extendName not in

    #{fileName}

and userId = #{userId}
and deleteFlag = 0

mapper.select:
select * from file_userfile
left join file_image on file_userfile.fileId = file_image.fileId

limit #{beginCount}, #{pageCount}

mapper.update:
UPDATE file_userfile set filePath = #{param2}
where filePath = #{param1} and fileName = #{param3}

    and extendName = #{param4}


    and extendName is null

and userId = #{param5}

mapper.insert:
insert into file_userfile ( deleteBatchNum, deleteFlag, deleteTime,
extendName, fileId, fileName, filePath, isDir, uploadTime, userId)
(select deleteBatchNum, deleteFlag, deleteTime, extendName, fileId,
fileName, #{newFilePath}, isDir, uploadTime, userId
from file_userfile


        and userId = #{userId}


        and fileName = #{fileName}


        and filePath = #{oldFilePath}



            and extendName = #{extendName}


            and isDir = 1




)
